(1801, 'options_hp_customer_rpt_0_hp_cam_nhan_customer_name', 'Chị Lương Vĩnh Phương Vy', 'no'),
(1802, '_options_hp_customer_rpt_0_hp_cam_nhan_customer_name', 'field_5c17187f7166d', 'no'),
(1803, 'options_hp_customer_rpt_0_hp_cam_nhan_customer_position', 'Giám đốc đào tạo Cty Loreal Việt Nam', 'no'),
(1804, '_options_hp_customer_rpt_0_hp_cam_nhan_customer_position', 'field_5c1718967166e', 'no'),
(1805, 'options_hp_customer_rpt_0_hp_cam_nhan_customer_message', 'Công dụng của Yến Sào thì có lẽ rất nhiều người biết đến, nhưng muốn tìm được một sản phẩm uy tín và chất lượng thì thật không dễ dàng trong thời buổi hiện nay. Bén duyên với công việc chuyên về mỹ phẩm và làm đẹp hơn 20 năm qua. Vi rất hiểu sức khỏe và làn da cần được chăm sóc như thế nào từ bên trong cho đến bên ngoài. Vi đã sử dụng Yến Sào khoảng 10 năm nay với nhiều nguồn khác nhau. Cho đến khi trong một dịp tình cờ dùng thử Yến Sào của Nàng Yến, thì Vi đã rất thích vì sợi yến dai, vị thanh ngọt, vi lại biết rõ nguồn cung cấp nên đã sử dụng trong 4 năm nay.', 'no'),
(1806, '_options_hp_customer_rpt_0_hp_cam_nhan_customer_message', 'field_5c1718a97166f', 'no'),
