(1809, 'options_hp_customer_rpt_1_hp_cam_nhan_customer_name', 'Chị Nguyễn Nữ Song Hà', 'no'),
(1810, '_options_hp_customer_rpt_1_hp_cam_nhan_customer_name', 'field_5c17187f7166d', 'no'),
(1811, 'options_hp_customer_rpt_1_hp_cam_nhan_customer_position', 'Giám Đốc Điều Hành Công ty Maper', 'no'),
(1812, '_options_hp_customer_rpt_1_hp_cam_nhan_customer_position', 'field_5c1718967166e', 'no'),
(1813, 'options_hp_customer_rpt_1_hp_cam_nhan_customer_message', 'Cô chủ của Nàng Yến là một người tính tình nhiệt tình, vui vẽ, làm việc có tính cam kết cao, và có trách nhiệm với lời nói Hơn nữa, cách em tư vấn khách hàng cực kỳ chuyên nghiệp và tận tâm,…\r\nTất cả đều thể hiện tại sản phẩm yên sào “ Nàng Yến” chỉnh chủ từ hình ảnh đến câu chuyện truyền thông, nhưng quan trọng nhất là chất lượng sản phẩm: ngon, thơm, tươi và nguyên chất. Một người tương đối khó và kỹ tính như ông xã Hà cũng đã có sự khen tặng cho sản phẩm này và đang là người sử dụng thường xuyên.\r\n', 'no'),
(1814, '_options_hp_customer_rpt_1_hp_cam_nhan_customer_message', 'field_5c1718a97166f', 'no'),
(1815, 'options_hp_customer_rpt_2_hp_cam_nhan_customer_name', 'Chị Huỳnh Thị Phấn', 'no'),
(1816, '_options_hp_customer_rpt_2_hp_cam_nhan_customer_name', 'field_5c17187f7166d', 'no'),
(1817, 'options_hp_customer_rpt_2_hp_cam_nhan_customer_position', 'Nội trợ', 'no'),
(1818, '_options_hp_customer_rpt_2_hp_cam_nhan_customer_position', 'field_5c1718967166e', 'no'),
(1819, 'options_hp_customer_rpt_2_hp_cam_nhan_customer_message', 'Nhà Phấn đã dùng rất nhiều nhãn hiệu yến từ được tặng đến nghe giới thiệu rồi mua. Nói về chất lượng thì phấn cũng không so sánh được cái nào với cái nào thì thấy na ná nhau,…\r\nGiá cả thì mình cũng không quan tâm lắm. Có điều sau khi dùng sản phẩm của Nàng Yến thì mình an tâm vì biết rỏ nguồn gốc, và tin tưởng “ Bà chủ”. Vậy thôi à!\r\n', 'no'),
